pImg = document.querySelector('#playerInputImg')
pInputRock = document.querySelector('#rock')
pInputPaper = document.querySelector('#paper')
pInputScissors = document.querySelector('#scissors')
pSide = document.querySelector('#playerSide')
completion = document.querySelector('#completion')
cSide = document.querySelector('#cpuSide')
versus = document.querySelector('#versus')

function playerChoice(e) {
    const playerDiv = document.createElement('img');
    playerDiv.setAttribute('class', 'img')
    if (e.target.id === "rock") {
            playerDiv.setAttribute('src', "data:image/jpeg;base64,") ;
            playerDiv.setAttribute('maxwidth', "250px")
            playerDiv.setAttribute('maxheight', '250px')
            
        }
            
    else if (e.target.id === "paper") {
            playerDiv.setAttribute('src', "data:image/jpeg;base64,")
            playerDiv.setAttribute('maxwidth', "250px")
            playerDiv.setAttribute('maxheight', '250px')
            
        }
    else if (e.target.id === "scissors"){
            playerDiv.setAttribute('src', 'data:image/jpeg;base64,')
            playerDiv.setAttribute('maxwidth', "250px")
            playerDiv.setAttribute('maxheight', '250px')
            
        }
        playerInputImg.appendChild(playerDiv);
        setTimeout(function (){
            playerDiv.classList.add('nonblur')
        }, 500)
        return e.target.id;
}



function computerChoice() {
    const cpuImg = document.createElement('img')
    cpuImg.setAttribute('class', 'img')
    let number = Math.floor(Math.random() * 3 + 1)
    if (number === 1){
        cpuImg.setAttribute('src', "data:image/jpeg;base64,") ;
        cpuImg.setAttribute('maxwidth', '250px')
        cpuImg.setAttribute('maxheight', '250px')
        cSide.appendChild(cpuImg);
        setTimeout(function (){
            cpuImg.classList.add('nonblur')
        }, 2000)
        return "Rock"
    }
    else if (number === 2){
        cpuImg.setAttribute('src', "data:image/jpeg;base64,")
        cpuImg.setAttribute('maxwidth', '250px')
        cpuImg.setAttribute('maxheight', '250px')
        cSide.appendChild(cpuImg);
        setTimeout(function (){
            cpuImg.classList.add('nonblur')
        }, 2000)
        return "Paper"
    }
    else {
        cpuImg.setAttribute('src', 'data:image/jpeg;base64,')
        cpuImg.setAttribute('maxwidth', '250px')
        cpuImg.setAttribute('maxheight', '250px')
        cSide.appendChild(cpuImg);
        setTimeout(function (){
            cpuImg.classList.add('nonblur')
        }, 2000)
        return "Scissors"
    }
    
}

function gameOver() {
    completion.innerText = "Game Over"
}


function runGame (pC, cC){
    console.log(pC);
    console.log(cC);
    if (pC === "rock"){
        if (cC === 'Paper'){
            completion.innerText = "get blanketed goober"
        }
        else if (cC === 'Rock'){
            completion.innerText = "*rock sounds*"
        }
        else if (cC === 'Scissors'){
            completion.innerText = 'SMASH SMASH SMASH SMASH SMASH'
        }
    }   
    else if (pC === "paper"){
        if (cC === 'Paper'){
            completion.innerText = "*paper sounds*"
        }
        else if (cC === 'Rock'){
            completion.innerText = "hide that lil slut away"
        }
        else if (cC === 'Scissors'){
            completion.innerText = 'Get fuken cut nerd'
        }
    }   
    else if (pC === "scissors"){
        if (cC === 'Paper'){
            completion.innerText = "cut dat ho"
        }
        else if (cC === 'Rock'){
            completion.innerText = "get smooshed"
        }
        else if (cC === 'Scissors'){
            completion.innerText = '*sounds of scissors clashing*'
        }
    }   
}


pSide.addEventListener('click', function(e) {
    e.preventDefault();
    let pC = playerChoice(e);
    versus.innerText = "VERSUS"
    let cC = computerChoice();
    runGame (pC, cC)
} )
  
    
    
    
    
